import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function CadastroTime() {
  const [integrantes, setIntegrantes] = useState({});
  const [nomeTime, setNomeTime] = useState('');
  const [dataSelecionada, setDataSelecionada] = useState('');

  useEffect(() => {
    // Fazer a solicitação para a API ao montar o componente
    axios.get('http://localhost:8080/duxus/time/cadastro')
      .then(response => {
        setIntegrantes(response.data);
      })
      .catch(error => {
        console.error('Erro ao obter os dados:', error);
      });
  }, []); // Executar apenas uma vez ao montar o componente

  const handleSubmit = (event) => {
    event.preventDefault();
    // Aqui você pode enviar os dados para a rota na API usando axios.post ou outra forma de envio de dados.
    // Você pode acessar os valores dos campos de nome e data selecionada a partir dos estados nomeTime e dataSelecionada, respectivamente.
  };

  return (
    <div>
      <h2>Cadastro de Time</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nome do Time:</label>
          <input
            type="text"
            value={nomeTime}
            onChange={(e) => setNomeTime(e.target.value)}
          />
        </div>
        <div>
          <label>Data:</label>
          <input
            type="date"
            value={dataSelecionada}
            onChange={(e) => setDataSelecionada(e.target.value)}
          />
        </div>
        <div>
          <h3>Lista de Integrantes</h3>
          {Object.keys(integrantes).map(id => (
            <div key={id}>
              <label>
                <input type="checkbox" value={id} />
                {integrantes[id].nome} - {integrantes[id].funcao}
              </label>
            </div>
          ))}
        </div>
        <button type="submit">Enviar</button>
      </form>
    </div>
  );
}
